mapper.update:
UPDATE MEMBER
   SET MEMBER_NO = #{memberNo}, MEMBER_ID = #{memberId}, MEMBER_PW = #{memberPw}, MEMBER_NAME = #{memberName}, MEMBER_PHONE = #{memberPhone}, MEMBER_EMAIL = #{memberEmail}, MEMBER_POSTCODE = #{memberPostcode}, MEMBER_ROAD_ADDRESS = #{memberRoadAddress}, MEMBER_DETAIL_ADDRESS = #{memberDetailAddress}, MEMBER_INFO_MODIFIED = SYSDATE()
 WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT A.* FROM (SELECT MEMBER_ID, MEMBER_NO, MEMBER_PW, MEMBER_NAME, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_POSTCODE, MEMBER_ROAD_ADDRESS, MEMBER_DETAIL_ADDRESS, MEMBER_SIGN_UP FROM MEMBER
${column} LIKE CONCAT('%', LOWER(IFNULL(#{query}, '')), '%') ${column} LIKE CONCAT('%', LOWER(IFNULL(#{query}, '')), '%') ${column} LIKE CONCAT('%', LOWER(IFNULL(#{query}, '')), '%') ${column} BETWEEN STR_TO_DATE(#{begin}, '%Y-%m-%d') AND STR_TO_DATE(#{end}, '%Y-%m-%d')
ORDER BY MEMBER_SIGN_UP ) A LIMIT #{beginRecord}, #{recordPerPage}

SELECT DISTINCT ${column}
  FROM MEMBER
 WHERE ${column} LIKE CONCAT(#{query}, '%')

