SELECT BOOK_NO, BOOK_ISBN, BOOK_TITLE, BOOK_AUTHOR, BOOK_PUBLISHER, BOOK_PUBDATE, BOOK_DESCRIPTION, BOOK_IMAGE, BOOK_FIELD
  FROM BOOK
 WHERE BOOK_ISBN = #{bookIsbn}

SELECT BOOK_NO, BOOK_ISBN, BOOK_TITLE, BOOK_AUTHOR, BOOK_PUBLISHER, BOOK_PUBDATE, BOOK_DESCRIPTION, BOOK_IMAGE, BOOK_FIELD
  FROM BOOK
 WHERE BOOK_NO = #{bookNo}

mapper.update:
UPDATE BOOK
   SET BOOK_ISBN = #{bookIsbn}, BOOK_TITLE = #{bookTitle}, BOOK_AUTHOR = #{bookAuthor}, BOOK_PUBLISHER = #{bookPublisher}, BOOK_PUBDATE = #{bookPubdate}, BOOK_DESCRIPTION = #{bookDescription}, BOOK_IMAGE = #{bookImage}, BOOK_FIELD = #{bookField}
 WHERE BOOK_NO = #{bookNo}

mapper.select:
SELECT A.* FROM (SELECT BOOK_NO, BOOK_ISBN, BOOK_TITLE, BOOK_AUTHOR, BOOK_PUBLISHER, BOOK_PUBDATE, BOOK_DESCRIPTION, BOOK_IMAGE, BOOK_FIELD FROM BOOK
LOWER(${column}) LIKE '%' || LOWER(#{query}) || '%' LOWER(${column}) LIKE '%' || LOWER(#{query}) || '%' LOWER(${column}) LIKE '%' || LOWER(#{query}) || '%' LOWER(${column}) LIKE '%' || LOWER(#{query}) || '%'
ORDER BY BOOK_PUBDATE) A LIMIT #{beginRecord}, #{recordPerPage}

SELECT DISTINCT ${column}
  FROM BOOK
 WHERE ${column} LIKE CONCAT(#{query}, '%')
